// SPDX-License-Identifier: None
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";

interface IOracle{
    function priceOf(address) external view returns(int,uint8);
}

interface mIERC20 is IERC20{
    function mint(address,uint) external;
}

contract TestDex{
    address weth;
    address stablecoin;
    IOracle oracle;

    constructor(address _oracle, address _stablecoin){
        oracle = IOracle(_oracle);
        stablecoin = _stablecoin;
        weth = address(new ERC20("WETH", "Test WETH"));
    }

    function WETH() external view returns(address){
        return weth;
    }


    function swapExactETHForTokens(
        uint amountOutMin, 
        address[] calldata path, 
        address to, 
        uint deadline
        )
        external
        payable
        returns (uint[] memory amounts)
    {
        require(path[0] == weth, "must swap WETH");
        require(path[1] == stablecoin, "Must be swapping to stablecoin");
        require(msg.value > 0, "Cannot swap 0");
        IERC20(path[1]).transfer(to, uint(_getPriceUnderlyingUSD(msg.value)));
        amounts = new uint[](path.length);
        amounts[0] = msg.value;
        amounts[1] = msg.value;
    }

    function swapExactTokensForETH(
        uint amountIn, 
        uint amountOutMin, 
        address[] calldata path, 
        address to, 
        uint deadline
        )
        external
        returns (uint[] memory amounts)
    {
        require(path[1] == weth, "must be swapping to WETH");
        require(path[0] == stablecoin, "Must swap stablecoin");
        IERC20(path[0]).transferFrom(msg.sender, address(this), amountIn);
        payable(msg.sender).transfer(uint(_getAmountOfUnderlyingForUSD(int(amountIn))));
        amounts = new uint[](path.length);
        amounts[0] = amountIn;
        amounts[1] = amountIn;
    }

    function addLiquidityETH(
        address token,
        uint amountTokenDesired,
        uint amountTokenMin,
        uint amountETHMin,
        address to,
        uint deadline
    ) external payable returns (uint amountToken, uint amountETH, uint liquidity){
        IERC20(token).transferFrom(msg.sender, address(this),amountTokenDesired);
    }


    function _getPriceUnderlyingUSD(uint _underlyingAm) public view returns(int){
        (int price, uint8 decimals) = oracle.priceOf(weth);
        return((int(_underlyingAm) * price) / int(10**decimals));
    }

    function _getAmountOfUnderlyingForUSD(int _amount) public view returns(int){
        (int price, uint8 decimals) = oracle.priceOf(weth);
        return((int(10**decimals) * (int(_amount)) / price));
    }


}

contracts/GovernanceToken.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.2;

import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Votes.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

contract GovernanceToken is ERC20Votes, Ownable {

    IERC20 public token;
    uint public devShare = 12 ether;

    constructor(address _token)
        ERC20("GovernanceToken", "GT")
        ERC20Permit("GovernanceToken")
        Ownable()
    {
        token = IERC20(_token);
        _mint(msg.sender, devShare);
    }

    // Leave the bar. Claim back your TOKENs.
    // Unlocks the staked + gained Token and burns xToken
    function leave(uint256 _share) external {
        // Gets the amount of xToken in existence
        uint256 totalShares = totalSupply();
        // Calculates the amount of Token the xToken is worth
        uint256 what = (_share * token.balanceOf(address(this))) / (totalShares);
        _burn(msg.sender, _share);
        token.transfer(msg.sender, what);
    }

    // silly little function to be able to withdrawal your earnings while keeping the base amount.
    // this only leaves with profits then gives you back enough shares to be equal to what you initial investment was
    function withdrawal() external{
        uint256 _share = token.balanceOf(msg.sender);
        // Gets the amount of xToken in existence
        uint256 totalShares = totalSupply();
        // Calculates the amount of Token the xToken is worth
        uint256 what = (_share * token.balanceOf(address(this))) / (totalShares);
        if(what > _share){
            token.transfer(msg.sender, what - _share);
            _burn(msg.sender, (_share * totalShares) / token.balanceOf(address(this)));
        }
    }

    function mint(address _to, uint256 _amount) external onlyOwner{
        _mint(_to, _amount);
    }

    // The functions below are overrides required by Solidity.

    function _afterTokenTransfer(
        address from,
        address to,
        uint256 amount
    ) internal override(ERC20Votes) {
        super._afterTokenTransfer(from, to, amount);
    }

    function _mint(address to, uint256 amount) internal override(ERC20Votes) {
        super._mint(to, amount);
    }

    function _burn(address account, uint256 amount)
        internal
        override(ERC20Votes)
    {
        super._burn(account, amount);
    }
}

